import React from 'react'

function FeedbackStats({ feedback }) {
    const counter = average(feedback)
  return (
    <div className='feedback-stats'>
        <h4>{feedback.length} Reviews</h4>
        <h4>Average Rating: {average(feedback)}</h4>
    </div>
  )
}

function getRating(feedback) {
    return feedback.map((item) => {
        return item.rating
    })
}

function average(feedback) {
    let sum = 0;
    var getRating = getRating(feedback)
    for (let i = 0; i < getRating.length; i++) {
        getRating[i] += sum
    }

    return sum/getRating.length
}

export default FeedbackStats
